/**
 * @file jQuery plugin that adds functionality to uncheck a radio button.
 * @author Renaat De Muynck <renaat.demuynck@gmail.com>
 * @license MIT
 */
$.fn.uncheckable=function(){'use strict';this.each(function(){var radio=$(this),parentLabel=radio.closest('label'),targets=$('label[for="'+this.id+'"]').add(parentLabel.length===1?parentLabel:radio);targets.mouseup(function(e){if(radio.prop('checked')&&e.which===1){radio.one('click',function(){radio.prop('checked',false);});}});});};